import React from 'react';
import { useState } from 'react';
import '../App.css';
import {useSelector, useDispatch} from "react-redux";
import {addtask} from "../action/index";


export const Input= (props) =>{

    const [InputList, setInputList] = useState("");
    const dispatch = useDispatch();

    const itemEvent = (event) => {
        //event.preventDefault();
        setInputList(event.target.value);
    };

    const keyevent=(event)=> {
        if (event.code === "Enter") {
          event.preventDefault();
          props.addtask(InputList)
        }
      }

    //const listofItems = (event) => {

        //props.addtask(InputList)

    //};


    return (
        <form>
           <input type = "Text" id = "task" onKeyDown = {(e) => keyevent(e)} onChange = {itemEvent}   ></input>
           <input type = "button" value="add" onClick = {() => dispatch(addtask(InputList))} ></input>
        </form>
    )
    //onKeyDown = {(e) => props.keyevent(e)}
};
