import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import {heading} from "./MyComponent/Heading";

function App() {

  const [InputList, setInputList] = useState("");
  const [active, setactive] = useState([]);
  const [alltk, setalltk] = useState([]);
  const [completed, setcompleted] = useState([]);

  const [showActive, setShowActive] = useState(true);
  const [showAlltask, setShowAlltask] = useState(false);
  const [showCompleted, setShowCompleted] = useState(false);

  const itemEvent = (event) => {
    //event.preventDefault();
    setInputList(event.target.value);
  };

  const keyevent=(event)=> {
    if (event.code === "Enter") {
      event.preventDefault();
      setactive((oldItems) => {
        return [...oldItems,InputList]
      });

      setalltk((oldItems) => {
        return [...oldItems,InputList]
      });
    }
  }

  const listofItems = (event) => {

    setactive((oldItems) => {
     return [...oldItems,InputList]
    });

    setalltk((oldItems) => {
      return [...oldItems,InputList]
    });


  };

  const alltask = () => {
    setShowCompleted(showCompleted => false);
    setShowActive(showActive => false);
    setShowAlltask(showAlltask => true);

  };

  const activetask = () => {
    setShowCompleted(showCompleted => false);
    setShowActive(showActive => true);
    setShowAlltask(showAlltask => false);

  };

  const completedtask = () => {
    setShowCompleted(showCompleted => true);
    setShowActive(showActive => false);
    setShowAlltask(showAlltask => false);
  };

  const delet = (id) => {
    setactive((oldItems) => {
      return oldItems.filter((arrElem, index) => {
        return index !== id;
      });
    });  

    setalltk((oldItems) => {
      return oldItems.filter((arrElem, index) => {
        return index !== id;
      });
    });  
  };

  const edit = (id) => {
    console.log("hello");

  };

  const complet = (id) => {

    console.log(active);
    setcompleted((oldItems) => {
      return [...oldItems,active[id]]
    });

    setactive((oldItems) => {
      return oldItems.filter((arrElem, index) => {
        return index !== id;
      });
    }); 

  };

  const compdelet = (id) => {
    setcompleted((oldItems) => {
      return oldItems.filter((arrElem, index) => {
        return index !== id;
      });
    });  

    setalltk((oldItems) => {
      return oldItems.filter((arrElem, index) => {
        return index !== id;
      });
    });  
  };



  return (
    <>

      <heading/>
      <form>
        <input type = "Text" id = "task" onKeyDown = {(e) => keyevent(e)} onChange = {itemEvent} ></input>
        <input type="button" value="add"  onClick = {listofItems}></input>
      </form>

      <button onClick = {alltask} class = "btn"> show all task </button>
      <button onClick = {activetask} class = "btn"> show active task </button>
      <button onClick = {completedtask} class = "btn"> show completed task </button>
      <div >


        {showActive && <h1><u>{active.length} Active Task</u></h1>}
        {showAlltask && <h1><u>{alltk.length} All Task</u></h1>}
        {showCompleted && <h1><u>{completed.length} Completed Task</u></h1>}

        {showActive && active.map((itemval,index) => {
          return <><ul><li> {itemval} </li> <button onClick =  {() => edit(index)} > edit </button> <button onClick =  {() => delet(index)} > delete </button> <button onClick =  {() => complet(index)} > completed </button></ul> </> ;
        })}

        {showAlltask && alltk.map((itemval,index) => {
          return <><ul><li> {itemval} </li> <button onClick =  {() => edit(index)} > edit </button> <button onClick =  {() => delet(index)} > delete </button></ul> </> ;
        })}

        {showCompleted && completed.map((itemval,index) => {
          return <><ul><li> {itemval} </li> <button onClick =  {() => edit(index)} > edit </button> <button onClick =  {() => compdelet(index)} > delete </button></ul> </> ;
        })}


      </div>


    </>
  );
}

export default App;
