export const addtask = (task) =>{
    return { 
        type : "add", 
        payload:task
    }
};

export const boolvalue = (num) =>{
    return { 
        type:"bool",
        payload:num
    }
};

export const completed = (id) =>{
    return { 
        type:"complete",
        payload:id
    }
};

export const deleted = (id) =>{
    return { 
        type:"delete",
        payload:id
    }
};
