const initialState = {
    alltask: [],
    activetask:[],
    completedtask:[]

};


const addtask = (state = initialState, action) =>{
    switch (action.type){
      case "add": return {...state,alltask:[...state.alltask,action.payload]};
      default: return state;
    }

};

export default addtask;
