export const addtask = (task) =>{
    return { 
        type : "add", 
        payload:task
    }
};

export const boolvalue = (num) =>{
    return { 
        type:"bool",
        payload:num
    }
};

export const completed = (id) =>{
    return { 
        type:"complete",
        payload:id
    }
};

export const compdelet = (id) =>{
    return { 
        type:"compdelete",
        payload:id
    }
};

export const delet= (id) =>{
    return { 
        type:"delete",
        payload:id
    }
};

export const edit= (id,val) =>{
    return { 
        type:"edit",
        id:id,
        val:val
    }
};
