import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import {Head} from "./MyComponent/Heading";
import {Input} from "./MyComponent/Input";
import {Button1} from "./MyComponent/Button1";
import {Button2} from "./MyComponent/Button2";
import {Button3} from "./MyComponent/Button3";  
import {TaskHead} from "./MyComponent/TaskHead";
import {Task} from "./MyComponent/Task";

import {useSelector, useDispatch} from "react-redux";

function App() {

const mystate = useSelector((state) => state.add);
console.log(mystate.activetask);

const [active, setactive] = useState([]);
const [alltk, setalltk] = useState([]);

const [completed, setcompleted] = useState([]);
const [showActive, setShowActive] = useState(true);
const [showAlltask, setShowAlltask] = useState(false);
const [showCompleted, setShowCompleted] = useState(false);

/*
const addtask = (value) =>{

    setactive((oldItems) => {
      return [...oldItems,value]
     });

    setalltk((oldItems) => {
      return [...oldItems,value]
    });
};
*/



const alltask = () => {
    setShowCompleted(showCompleted => false);
    setShowActive(showActive => false);
    setShowAlltask(showAlltask => true);

};

const activetask = () => {
    setShowCompleted(showCompleted => false);
    setShowActive(showActive => true);
    setShowAlltask(showAlltask => false);

};

const completedtask = () => {
    setShowCompleted(showCompleted => true);
    setShowActive(showActive => false);
    setShowAlltask(showAlltask => false);
};

const delet = (id) => {
    setactive((oldItems) => {
      return oldItems.filter((arrElem, index) => {
        return index !== id;
    });
});  

    setalltk((oldItems) => {
        return oldItems.filter((arrElem, index) => {
            return index !== id;
        });
    });  
};

const edit = (id) => {
    console.log("hello");
};

const complet = (id) => {

    console.log(active);
    setcompleted((oldItems) => {
      return [...oldItems,active[id]]
    });

    setactive((oldItems) => {
      return oldItems.filter((arrElem, index) => {
        return index !== id;
      });
    }); 
};

const compdelet = (id) => {
    setcompleted((oldItems) => {
      return oldItems.filter((arrElem, index) => {
        return index !== id;
      });
    });  

    setalltk((oldItems) => {
      return oldItems.filter((arrElem, index) => {
        return index !== id;
      });
    });  
};

  return (
    <>

      <Head/>
      <Input/>
      <Button1 alltask = {alltask}/>
      <Button2 activetask = {activetask}/>
      <Button3 completedtask = {completedtask}/>  


      <div >

        {showActive && <TaskHead TH = {{ length: active.length, desc:"Active Task"}}/>}
        {showAlltask && <TaskHead TH = {{ length: alltk.length, desc:"All Task"}}/>}
        {showCompleted && <TaskHead TH = {{ length: completed.length, desc:"Completed Task"}}/>}

        {showActive && active.map((itemval,index) => {
          return <><Task list = {{index:index, val:itemval,fun1:edit,fun2:delet,fun3:complet,bool:true}}/></>
        })}

        {showAlltask && alltk.map((itemval,index) => {
          return <><Task list = {{index:index, val:itemval,fun1:edit,fun2:delet,bool:false}}/></>
        })}

        {showCompleted && completed.map((itemval,index) => {
          return <><Task list = {{index:index, val:itemval,fun1:edit,fun2:compdelet,bool:false}}/></>
        })}

      </div>


    </>
  );
}

export default App;
