const initialState = {
    alltask: [],
    activetask:[],
    completedtask:[]

};


const addtask = (state = initialState, action) =>{
    //console.log("hello");
    switch (action.type){
      case "add": return {...state,alltask:[...state.alltask,action.payload],activetask:[...state.alltask,action.payload]};
      default: return state;
    }

};

//export default addtask;

const initialState1 = 0

export const boolvalue = (state = initialState1,action) =>{
  state = action.payload
};

