import React from 'react';
import '../App.css';
import {useDispatch} from "react-redux";
import {delet,completed,compdelet,editactive,editcompleted} from "../actions/index";


export const Task = (list) =>{
    const dispatch = useDispatch();
    //console.log(list.list.fun)
    return (
        <>
        <ul>
            <li> {list.list.val} </li> 
            <button onclick = {() => {list.list.fun1 === "active" ? dispatch(editactive(list.list.index,prompt("edittask"))):dispatch(editcompleted(list.list.index,prompt("edit task")))}}>edit</button>
            <button onClick =  {() => {list.list.fun === "delet" ? dispatch(delet(list.list.index)):dispatch(compdelet(list.list.index))}} > delete </button> 
            {list.list.bool ?<button onClick =  {() => dispatch(completed(list.list.index))} > completed </button> : ""}    
            </ul> 
        </>   
    )
};
